import React, {useState} from 'react';
import './App.css'

interface todos {
    
    todoText:any;
    index:any

}

const DisplayInfo: React.FC<todos> = ({todoText, index})=>{
    return(
        <div className="todo">

            {todoText.text}
        </div>

    )


}
interface addT{
    addTodo :(val:string)=>void;
}

const TodoInput: React.FC<addT>= ({addTodo})=> {
    const [val,setVal] = useState('');
    const submitForm = (e:React.FormEvent<HTMLInputElement>): void => {
        e.preventDefault();
        if (!val) return;
        addTodo(val);
        setVal('');
        
    }
    return(

        <form onSubmit={submitForm}>
            <input type="text" className="input" value={val} onChange={e => setVal(e.target.value)} />
        </form>
    )   
}    









// const handleSubmit=(e,todos,setTodos,input,setInput)=>{
//     e.preventDefault()
//     setTodos([...todos,{item: input,isCompleted:false}])
//     setInput("")
//   }
  
  



export const Todo: React.FC =()=>{
const [todos, setTodos] = useState<[{text:string;isCompleted:boolean}]>([
    {
    text:'sal',
    isCompleted:false 
    },
])

const addTodo= (text:string)=> {
    // const newTodos = [...todos,{ text:"hhg",isCompleted:false }]
    // setTodos(newTodos);
    setTodos([{...todos, text:text,isCompleted:false }])
}

return(
    <div className='todo-list'>
        {todos.map((todo,index)=>(
            <DisplayInfo  index={index} todoText={todo} />
            
        ))}
        <TodoInput addTodo={addTodo}/>
    </div>
        


)

}
