import React, {useState} from 'react';


interface todos {
    key:number;
    todoText:object;
    index:any

}

const DisplayInfo: React.FC = ({todoText, index}:todos)=>{
    return(
        <div className="todo">

            {todoText.text}
        </div>

    )


}

export const Todo: React.FC =()=>{
const [todos, setTodos] = useState<[{text:string;isCompleted:boolean}]>([
    {
    text:'sal',
    isCompleted:false 
    },


])
return(
    <div className='todo-list'>
        {todos.map((todo,idx)=>(
            <DisplayInfo key={idx} todo={todo} />
        ))}
    </div>
        


)

}
