import React, {useState} from 'react';
import './App.css'

interface todos {
    
    todoText:any;
    index:any
    completeTodo:(index:number)=>void;
}

const DisplayInfo: React.FC<todos> = ({todoText, index,completeTodo})=>{
    // if (todoText.text=='') return ''
    return(
        <div className="todo" style={{textDecoration:todoText.isCompleted ? 'line-through':''}} >

            
                <ul onClick={()=>completeTodo(index)}>
                    <li> {todoText.text}</li>
                </ul>

           
        </div>

    )


}
interface addT{
    addTodo :(val:string)=>void;
}

const TodoInput: React.FC<addT>= ({addTodo})=> {
    const [val,setVal] = useState('');
    console.log(val);
    const submitForm = (e:any) => {
        console.log("submitForm called")
        e.preventDefault();
        if (!val) return;
        addTodo(val);
        console.log(val);
        setVal('');
        
    }
    return(
        // <form>

        <form onSubmit={submitForm}>
            <input type="text" placeholder="add toDo..." className="input" value={val} onChange={e => setVal(e.target.value)} />
            {/* <button onClick={submitForm}>click</button> */}
        </form>
    )   
}    

interface counter{
    todos:any;
    fn:(todos:any)=>void 
}
// const Count: React.FC<counter> =({todos,fn})=>{
//     const totalTask=0;
//     const taskCompleted = 0
//     const keys = Object.keys(todos)
//     for (const key of keys) {
//         if keys.isCompleted == 
//       }
    
// }


export const Todo: React.FC =()=>{
let savedItems:any = localStorage.getItem('todos');
savedItems = JSON.parse(savedItems);
let dummy:any =[{text:'',isCompleted:false}]
const [todos, setTodos] = useState<any>(savedItems||dummy)
console.log(todos);

const addTodo= (text:string)=> {
    console.log("=======================>")
    let newTodos = [...todos,{ text:text,isCompleted:false }]
    if (todos.text==''){
        newTodos = [{ text:text,isCompleted:false }]
        
    }
    setTodos(newTodos);
    localStorage.setItem('todos', JSON.stringify(newTodos));
}


const completeTodo = (index:number) =>{
    const newTodos = [...todos]
    newTodos[index].isCompleted = true;
    setTodos(newTodos);
}



return(
    <div className='todo-list'>
        {todos.map((todo:any,index:number)=>(
            <DisplayInfo  index={index} todoText={todo} completeTodo={completeTodo} />
            
        ))}
        {/* <Enumerate /> */}
        <TodoInput addTodo={addTodo}/>
    </div>
        


)

}
