import React, {useState} from 'react';
import './App.css'

interface todos {
    
    todoText:any;
    index:any

}

const DisplayInfo: React.FC<todos> = ({todoText, index})=>{
    return(
        <div className="todo">

            {todoText.text}
        </div>

    )


}
interface addT{
    addTodo :(val:string)=>void;
}

const TodoInput: React.FC<addT>= ({addTodo})=> {
    const [val,setVal] = useState('');
    console.log(val);
    const submitForm = (e:React.FormEvent<HTMLInputElement>) => {
        console.log("submitForm called");
        e.preventDefault();
        if (!val) return;
        addTodo(val);
        console.log(val);
        setVal('');
        
    }
    return(
        <form onSubmit={(e)=>submitForm}>

        {/* <form onSubmit={submitForm}> */}
            <input type="text" className="input" value={val} onChange={e => setVal(e.target.value)} />
            <input type="submit" />
        </form>
    )   
}    









// const handleSubmit=(e,todos,setTodos,input,setInput)=>{
//     e.preventDefault()
//     setTodos([...todos,{item: input,isCompleted:false}])
//     setInput("")
//   }
  
  



export const Todo: React.FC =()=>{
const [todos, setTodos] = useState<[{text:string;isCompleted:boolean}]>([
    {
    text:'',
    isCompleted:false 
    },
])

const addTodo= (text:string)=> {
    console.log("===========>")
    const newTodos:[{text:string;isCompleted:boolean}] = [...todos,{ text:text,isCompleted:false }]
    // setTodos(newTodos);
    setTodos(newTodos)
}

return(
    <div className='todo-list'>
        {todos.map((todo,index)=>(
            <DisplayInfo key={index}  index={index} todoText={todo} />
            
        ))}
        <TodoInput addTodo={addTodo}/>
    </div>
        


)

}
